INSERT INTO products (id, name, platform, slug, category, release_date, metadata, created_at, updated_at, uid, synopsis, primary_platform_family, popularity_score, rating, freshness_score, external_ids) VALUES (2285,'Final Fantasy XVI [SteelBook]','PlayStation 5','final-fantasy-xvi','Game','2023-06-22 00:00:00','{"sources":{"thegamesdb":{"external_id":116001,"category":"Game","platform":"PlayStation 5","players":"1","last_synced_at":"2025-11-04T17:54:41+00:00","overview":"Steer Clive as he seeks to destroy the sacred Mothercrystals of Valisthea and battle again Dominants and Eikons through innovative and immersive gameplay.\r\nExperience Titanic Clashes when rival Dominants go head to head and their Eikons battle to the death\r\nEnjoy Eikonic Action when Clive utilizes the powers of multiple Eikons in breathtaking battles","search_queries":["Final Fantasy XVI","FF16"]}},"platforms":["PlayStation 5"]}','2025-11-04 17:55:56','2025-11-04 19:43:07','7b17ce3d051c708794e7afcc8ce2e17696c608df2ed7d0f68e8b5d53199d7a21','F1 2000 allows you to take on the role of any of the 2000 FIA Formula One Championship drivers. All of the tracks used during the 2000 season are also included, even the US Grand Prix at Indianapolis.

