INSERT INTO products (id, name, platform, slug, category, release_date, metadata, created_at, updated_at, uid, synopsis, primary_platform_family, popularity_score, rating, freshness_score, external_ids) VALUES (2301,'Ryu ga Gotoku 8','PC','like-a-dragon-infinite-wealth','Game','2024-01-26 00:00:00','{"sources":{"thegamesdb":{"external_id":122980,"category":"Game","platform":"PC","players":"1","last_synced_at":"2025-11-04T17:55:22+00:00","search_queries":["Like a Dragon Infinite Wealth","Yakuza Infinite Wealth"]}},"platforms":["PC"]}','2025-11-04 17:55:57','2025-11-04 19:43:08','dae41470f19007e5878f01968ccd5a472aff5464a3f2e4e837178d62af3b8606','EPIC EMOTIONAL DRAMA
