INSERT INTO products (id, name, platform, slug, category, release_date, metadata, created_at, updated_at, uid, synopsis, primary_platform_family, popularity_score, rating, freshness_score, external_ids) VALUES (2334,'Zelda no Densetsu: Toki no Ocarina','Nintendo 64','the-legend-of-zelda-ocarina-of-time','Game','1998-11-21 00:00:00','{"sources":{"thegamesdb":{"external_id":87437,"category":"Game","platform":"Nintendo 64","players":"1","last_synced_at":"2025-11-04T17:55:44+00:00","overview":"Ganondorf, the evil King of Thieves, is on the move, threatening the peaceful land of Hyrule.  He is determined to steal his way into the legendary Sacred Realm in hopes of harnessing the power of the mythical Triforce.  As the young hero Link, it is your destiny to thwart Ganondorf\u2019s evil schemes.  Navi, your guardian fairy, will guide you as you venture through the many regions of Hyrule, from the volcanic caves of Death Mountain to the treacherous waters of Zora\u2019s Domain.  Before you complete this epic quest, you\u2019ll delve into deadly dungeons, collect weapons of great power and learn the spells you need to conquer the most irresistible force of all-time.\r\n\r\n\u2022 The immersive storyline and environments draw players into an amazing 3D world.\r\n\u2022 Time travel allows you to play as Link in different stages of his life.\r\n\u2022 New gameplay features include a unique targeting system and 1st and 3rd person perspectives.\r\n\u2022 Up to three games can be saved simultaneously to memory!","search_queries":["The Legend of Zelda Ocarina of Time","Zelda Ocarina of Time"]}},"platforms":["Nintendo 64"]}','2025-11-04 17:55:59','2025-11-04 19:43:09','2e4cd84b7eb3cc3222bba87f44a4f276c9bf4ed7d8fc48745b16931401013a29','The Legend of Zelda: Collector''s Edition is a compilation of several installments from The Legend of Zelda series. It features both games released for the NES and Nintendo 64, with the former including revised texts fixing the original mistranslations