INSERT INTO products (id, name, platform, slug, category, release_date, metadata, created_at, updated_at, uid, synopsis, primary_platform_family, popularity_score, rating, freshness_score, external_ids) VALUES (55,'Zelda no Densetsu: Tears of the Kingdom','Nintendo Switch','zelda-tears-of-the-kingdom','Game','2023-05-12 00:00:00','{"publisher":"Nintendo","developer":"Nintendo EPD","sources":{"rawg":{"rawg_slug":"the-legend-of-zelda-tears-of-the-kingdom","synced_at":"2025-11-04T19:43:05+00:00"},"thegamesdb":{"external_id":114116,"category":"Game","platform":"Nintendo Switch","players":"1","last_synced_at":"2025-11-04T17:54:21+00:00","overview":"An epic adventure across the land and skies of Hyrule awaits in The Legend of Zelda\u2122: Tears of the Kingdom for Nintendo Switch\u2122. The adventure is yours to create in a world fueled by your imagination.\r\n\r\nIn this sequel to The Legend of Zelda: Breath of the Wild, you\u2019ll decide your own path through the sprawling landscapes of Hyrule and the mysterious islands floating in the vast skies above. Can you harness the power of Link\u2019s new abilities to fight back against the malevolent forces that threaten the kingdom?","search_queries":["The Legend of Zelda: Tears of the Kingdom","Zelda Tears of the Kingdom"]}},"platforms":["Nintendo Switch"]}','2025-11-04 15:01:03','2025-11-04 19:43:09','e0712a4f6a1b80345c71f058dc1950f9b6587f38690454da3f9c6829a0980410','Embark on a sprawling adventure across the skies and surface of Hyrule, crafting new abilities to fend off a looming calamity.','nintendo',0.92,96,0.1,'{"rawg":"the-legend-of-zelda-tears-of-the-kingdom","thegamesdb":"114116"}') ON CONFLICT (id) DO NOTHING;
INSERT INTO products (id, name, platform, slug, category, release_date, metadata, created_at, updated_at, uid, synopsis, primary_platform_family, popularity_score, rating, freshness_score, external_ids) VALUES (56,'Marvel''s Spider-Man [Collector''s Edition]','PlayStation 5','marvels-spider-man-2','Game','2018-09-07 00:00:00','{"publisher":"Sony Interactive Entertainment","developer":"Insomniac Games","sources":{"rawg":{"rawg_slug":"marvels-spider-man-2","synced_at":"2025-11-04T19:43:05+00:00"},"thegamesdb":{"external_id":61067,"category":"Game","platform":"PlayStation 5","players":"1","last_synced_at":"2025-11-04T17:54:37+00:00","overview":"Marvel''s Spider-Man, commonly referred to as Spider-Man, is an action-adventure game developed by Insomniac Games and published by Sony Interactive Entertainment for the PlayStation 4, based on the Marvel Comics superhero Spider-Man. Released worldwide on September 7, 2018, it is the first licensed game developed by Insomniac. The game tells a new story about Spider-Man that is not tied to an existing comic book, video game, or film, and covers both the Peter Parker and Spider-Man aspects of the character.\r\n\r\nSpider-Man was well received upon release. Critics hailed it as one of the best superhero games ever made, praising its gameplay, particularly the combat and web-swinging mechanics, graphics, story, soundtrack, and design of New York City, while expressing criticism for a lack of innovation in its open-world design. ","search_queries":["Marvel''s Spider-Man 2","Spider-Man 2 PS5"]}},"platforms":["PlayStation 5"]}','2025-11-04 15:01:03','2025-11-04 19:43:08','d532e5b74a8e6e54f2fba8791cad3ccc8539f952ba8ba60931fbaab71fa5ea41','Swing across Marvel’s New York as Peter Parker and Miles Morales while mastering symbiote powers to stop new threats.','playstation',0.89,94,0.1,'{"rawg":"marvels-spider-man-2","thegamesdb":"61067"}') ON CONFLICT (id) DO NOTHING;
INSERT INTO products (id, name, platform, slug, category, release_date, metadata, created_at, updated_at, uid, synopsis, primary_platform_family, popularity_score, rating, freshness_score, external_ids) VALUES (57,'Stray Blade','Xbox Series X','starfield','Game','2023-04-20 00:00:00','{"publisher":"Bethesda Softworks","developer":"Bethesda Game Studios","sources":{"rawg":{"rawg_slug":"starfield","synced_at":"2025-11-04T19:43:05+00:00"},"thegamesdb":{"external_id":123438,"category":"Game","platform":"Xbox Series X","players":"1","last_synced_at":"2025-11-04T17:54:55+00:00","overview":"Prove yourself in this action-adventure and master intense combat while exploring the ancient ruins of a mysterious civilization. Legends tell of Acrea the Lost Valley, a wild and overgrown place but unmistakably powerful. You found this forgotten land yet died. Time passes, and miraculously you are brought back to life. The price you pay: You are bound to this land.\r\n\r\nRegain your freedom and embark on the quest of restoring balance in this ravaged and war-torn place guided by your trusty companion, Boji. Explore towering throne rooms of giant god-kings and long-lost cities. As you uncover their secrets, prepare to face even deadlier foes. Get ready to explore a world that is constantly altered by your discoveries and the waging forces! Set off for an unforgettable adventure.\r\n\r\nTHE UNTOLD STORY: Embark on an unforgettable journey and discover the story of Acrea and its ancient legacy. Discover the whole history that turned the peaceful valley into a place of war and death. Use this knowledge to destroy the relics of the past and restore peace in the war-torn valley.\r\n\r\nFULL COMBAT CONTROL: The hyper-responsive combat system allows quick reactions and precise attacks to fully control the flow of battle. It merges strategic anticipation of enemy attacks with split-second blade-to-blade reactions \u2014 the High Noon of melee weapon combat.\r\n\r\nCHANGING WORLD: Change is part of your journey where every victory leaves a mark in the world. Time keeps moving forward even if you die. Revisit the places of former victory and experience the changes you brought to the world. But beware! Your actions will lead to even greater challenges.\r\n\r\nEXPLORE THE LOST VALLEY: Acrea''s colorful world ranges from overgrown city ruins to frozen caves and winding canyons. Together with your companion, explore the deepest corners of an ever-changing world bristling with adventure.\r\nEvery discovery holds treasures. Find rare materials, recipes, weapons, lore pieces, and beautiful places.\r\n\r\nEPIC BOSS FIGHTS: Encounter the God-Kings that ruined the land in the past. Bring their legacy to fall and free the land from the shadows of the old era. Harvest their powers and use them to unravel the mysteries of Acrea. New domains are waiting to be unlocked.\r\n\r\nPROGRESSION: Both the adventurer and Boji have individual talent trees.\r\nThe Adventurer is a fighter who gains experience through battle, whereas Boji is a craftsman who increases his experience by finding pieces of ancient lore.","search_queries":["Starfield"]}},"platforms":["Xbox Series X"]}','2025-11-04 15:01:03','2025-11-04 19:43:09','3c9fa92adf404f55dee50becd26420a4655a79bc3c1dd9a00bdedcedae7f787e','Explore the Settled Systems as a customizable explorer in Bethesda’s first new universe in 25 years.','xbox',0.85,88,0.1,'{"rawg":"starfield","thegamesdb":"123438"}') ON CONFLICT (id) DO NOTHING;
