INSERT INTO products (id, name, platform, slug, category, release_date, metadata, created_at, updated_at, uid, synopsis, primary_platform_family, popularity_score, rating, freshness_score, external_ids) VALUES (2275,'Chrono Trigger: Schala Edition','Super Nintendo','chrono-trigger','Game','2023-03-19 00:00:00','{"sources":{"thegamesdb":{"external_id":128813,"category":"Game","platform":"Super Nintendo","players":"1","last_synced_at":"2025-11-04T17:55:47+00:00","search_queries":["Chrono Trigger"]}},"platforms":["Super Nintendo"]}','2025-11-04 17:55:56','2025-11-04 19:43:06','ea89b286cc9ad0a651c60473e45c18d1420027db89ed6f34a8300c688fa83201','Chrono Trigger is a 1995 SNES video game developed by Square and released by Square Soft Inc. in the USA on August 11, 1995.

