INSERT INTO products (id, name, platform, slug, category, release_date, metadata, created_at, updated_at, uid, synopsis, primary_platform_family, popularity_score, rating, freshness_score, external_ids) VALUES (2308,'Mortal Kombat XL','PC','mortal-kombat-1-2023','Game','2016-03-01 00:00:00','{"sources":{"thegamesdb":{"external_id":39833,"category":"Game","platform":"PC","players":"2","last_synced_at":"2025-11-04T17:55:25+00:00","overview":"Mortal Kombat\u2122 XL is NetherRealm Studios\u2019 next highly anticipated installment in its legendary, critically acclaimed fighting game franchise that propels the iconic franchise into a new generation. Following on from the global success of Mortal Kombat\u2122 X, the game combines all the excitement and action from Mortal Kombat\u2122 X with brand new content that isn\u2019t available in the standard edition, allowing for more grueling action and vicious fatalities. For the first time, Mortal Kombat\u2122 XL introduces 4 new DLC characters into the mix along with a brand new environment, the Pit. Enjoy next-generation fatalities in high definition and step into an epic original story mode that pushes Mortal Kombat\u2122 25 years into the future!","search_queries":["Mortal Kombat 1","MK1 2023"]}},"platforms":["PC"]}','2025-11-04 17:55:58','2025-11-04 19:43:08','9d13a1b437381ffa791010f45b17c09257b0a6ca2e5b9865bab951cc974706d7','Discover a reborn Mortal Kombat Universe created by the Fire God Liu Kang. Mortal Kombat 1 ushers in a new era of the iconic franchise with a new fighting system, game modes, and fatalities!

